import React, { useEffect, useMemo, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { deleteUser, listUsers } from "../actions/userActions";

export default function Users(props) {
  const dispatch = useDispatch();
  const usersList = useSelector((state) => state.usersList);
  const { loading, error, users } = usersList;

  const [totalItems, setTotalItems] = useState(0);
  const [currentPage, setCurrentPage] = useState(1);
  const [search, setSearch] = useState("");

  const ITEMS_PER_PAGE = 5;

  const userDelete = useSelector((state) => state.userDelete);

  const {
    loading: loadingDelete,
    error: errorDelete,
    success: successDelete,
  } = userDelete;

  useEffect(() => {
    dispatch(listUsers());
  }, [dispatch]);

  const usersData = useMemo(() => {
    let computedUsers = users;

    return computedUsers.slice(
      (currentPage - 1) * ITEMS_PER_PAGE,
      (currentPage - 1) * ITEMS_PER_PAGE + ITEMS_PER_PAGE
    );
  }, [currentPage, users]);

  const deleteHandler = (user) => {
    if (window.confirm("Are You sure..! You want to delete the User..?")) {
      dispatch(deleteUser(user._id));
    }
  };

  return (
    <div className="conatiner mt-5">
      {loadingDelete && <p>loading...</p>}
      {errorDelete && <p>{errorDelete}</p>}
      {successDelete && <p>User Deleted Successfully</p>}
      {loading ? (
        <p>loading..</p>
      ) : error ? (
        <p>{error}</p>
      ) : (
        <table className="table table-striped table-bordered table-hover">
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Id</th>
            <th>Phone Number</th>
            <th>Is Admin</th>
            <th>Actions</th>
          </tr>
          {users?.map((user) => (
            <tr key={user._id}>
              <td>{user.name}</td>
              <td>{user.email}</td>
              <td>{user._id}</td>
              <td>{user.phoneNumber}</td>
              <td>{user.isAdmin ? "YES" : "NO"}</td>
              <td>
                <button
                  type="button"
                  className="btn btn-outline-primary ml-2 my-2 my-sm-0"
                  onClick={() =>
                    props.history.push(`/user/${user._id}/updateUser`)
                  }
                >
                  ✏
                </button>
                {/* (...) */}
                {/* btn btn-outline-danger btn-sm */}
                <button
                  type="button"
                  className="btn btn-outline-danger ml-2 my-2 my-sm-0"
                  onClick={() => deleteHandler(user)}
                >
                  🗑
                </button>
              </td>
            </tr>
          ))}
        </table>
      )}
    </div>
  );
}
